document.getElementById("menuBtn").addEventListener("click",(function(){const e=document.querySelector(".header__inner"),t=document.getElementById("menuBtn"),i=document.body;if(e.classList.contains("header__inner--active"))e.style.height="90px",i.classList.remove("body--active");else{e.style.height="auto";const t=e.clientHeight;e.style.height="90px",setTimeout((()=>{e.style.height=t+"px",i.classList.add("body--active")}),0)}e.classList.toggle("header__inner--active"),t.classList.toggle("active")}));